<?php	$name = filter_input(INPUT_POST, 'name', FILTER_SANITIZE_SPECIAL_CHARS);	$email = filter_input(INPUT_POST, 'email', FILTER_SANITIZE_SPECIAL_CHARS);	$message = $_POST['message'];	$to = "admin@banjananurseryschool.com";	$subject = "Email sent from www.banjananurseryschool.com";	$headers = "From: admin@banjananurseryschool.com" . "\r\n" .	"CC: admin@banjananurseryschool.com";		$message = str_ireplace ("'","&#39;",$message);	$message = "From: ".$name."\r\n"."Email: ".$email."\r\n\r\n".$message;	if (!mail( $to, $subject, $message, $headers)){		header('Location: ../contact.php?sent=2');	} else {		header('Location: ../contact.php?sent=1');	}?>